import React, { useEffect, useState } from 'react';
import '../css/projects.css';
import '../css/style.css';

const MAX_DESCRIPTION_LENGTH = 200; // Maksymalna długość opisu przed rozwinięciem

const Projects = () => {
  const [allProjects, setAllProjects] = useState([]);
  const [displayedProjects, setDisplayedProjects] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch("http://localhost:8000/app/projects/")
      .then(response => response.json())
      .then(data => {
        setAllProjects(data);
        setDisplayedProjects(data);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  const handleSearchKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  const handleSearch = () => {
    const filteredProjects = allProjects.filter(project =>
      project.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      project.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setDisplayedProjects(filteredProjects);
  };

  const handleDelete = async (projectId) => {
    try {
      await fetch(`http://localhost:8000/app/projects/delete/${projectId}/`, {
        method: 'DELETE',
      });
      setAllProjects(allProjects.filter(project => project.id !== projectId));
      setDisplayedProjects(displayedProjects.filter(project => project.id !== projectId));
    } catch (error) {
      console.error('Error deleting project', error);
    }
  };

  const handleExpand = (projectId) => {
    // Implementuj logikę rozwinięcia opisu
    // Na przykład, możesz użyć lokalnego stanu do śledzenia rozwiniętych projektów
    console.log(`Rozwiń projekt o ID: ${projectId}`);
  };

  return (
    <div>
      <div className="naviBar">
        {/* ... (niezmienione części) ... */}
      </div>

      <div className="hello">
        <br /><br />
        <h1>CorkCulture</h1>
        <h2>Out of love for wine.</h2>
      </div>

      <br /><br /><br />

      <div className="mainPage">
        <section className="projectsSection">
          {displayedProjects.map((project) => (
            <div key={project.id} className="vineyardSection projectItem">
              <img className="vineyardPhoto" src={project.file} alt="Vineyard" />
              <div>
                <h2>{project.title}</h2>
                <p className="project-description">
                  {project.description.length > MAX_DESCRIPTION_LENGTH
                    ? `${project.description.substring(0, MAX_DESCRIPTION_LENGTH)}...`
                    : project.description}
                </p>
                {project.description.length > MAX_DESCRIPTION_LENGTH && (
                  <button className="expand-btn" onClick={() => handleExpand(project.id)}>
                    Rozwiń
                  </button>
                )}
                <div className="project-tags">
                  {project.tags && project.tags.map(tag => (
                    <span key={tag.name} className="project-tag">{tag.name}</span>
                  ))}
                </div>
                <div className="icons">
                  <i className="fa-regular fa-thumbs-up"></i>
                  <i className="fa-regular fa-thumbs-down"></i>
                  <button onClick={() => handleDelete(project.id)} className="delete-btn">
                    Usuń
                  </button>
                </div>
              </div>
            </div>
          ))}
        </section>
      </div>
    </div>
  );
};

export default Projects;
