import React, { useEffect, useState } from 'react';
import '../css/projects.css';
import '../css/style.css';

const Projects = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Używamy poprawionego endpointu API do pobrania projektów
    fetch("http://localhost:8000/app/projects/")
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div>
      <div className="naviBar">
        {/* Twoja istniejąca nawigacja */}
        <nav className="dropdownmenu">
          {/* ... */}
        </nav>

        <header className="normalHeader">
          {/* ... */}
        </header>
      </div>

      <div className="hello">
        {/* Twoje istniejące powitanie */}
        <br /><br />
        <h1>CorkCulture</h1>
        <h2>Out of love for wine.</h2>
      </div>

      <br /><br /><br />

      <div className="mainPage">
        <section className="projectsSection">
          {projects.map((project) => (
            <div key={project.id} className="vineyardSection">
              <img className="vineyardPhoto" src={`public/uploads/${project.file}`} alt="Vineyard" />
              <div>
                <h2>{project.title}</h2>
                <p>{project.description}</p>
                {/* Zakładam, że pola like i dislike nie są częścią twojego modelu w Django, jeśli są, dodaj je tutaj */}
                <div className="icons">
                  <i className="fa-regular fa-thumbs-up"></i>
                  <i className="fa-regular fa-thumbs-down"></i>
                </div>
              </div>
            </div>
          ))}
        </section>
      </div>

      <footer>Copyright ©</footer>
    </div>
  );
};

export default Projects;
