import React, { useEffect, useState } from 'react';
import '../css/projects.css';
import '../css/style.css';

const Projects = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Używamy endpointu API z Django do pobrania projektów
    fetch("http://localhost:8000/app/projects/")
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div>
      <div className="naviBar">
        {/* Twoja nawigacja i header */}
      </div>

      <div className="hello">
        {/* Powitanie i inne elementy */}
      </div>

      <div className="mainPage">
        <section className="projectsSection">
          {projects.map((project) => (
            <div key={project.id} className="vineyardSection">
              <img className="vineyardPhoto" src={`public/uploads/${project.file}`} alt="Vineyard" />
              <div>
                <h2>{project.title}</h2>
                <p>{project.description}</p>
                {/* Tutaj możesz dodać inne elementy związane z projektem, jak polubienia czy niepolubienia */}
              </div>
            </div>
          ))}
        </section>
      </div>

      <footer>Copyright ©</footer>
    </div>
  );
};

export default Projects;
